hudson.tasks.Shell.command:
#!/bin/bash

export ARCHIVEDIR=/build/buildmt/hudson-home/jobs/BridgePoint/lastSuccessful/archive
export DATADIR=data
export BPLOG=${DATADIR}/buildfiles.html

ver=$(grep -o "[0-9]\.[0-9]\.[0-9]" /tmp/buildinfo/about.mappings)
curdate=$(grep -o "[0-9][0-9][0-9][0-9]-[0-9][0-9]-[0-9][0-9] [0-9][0-9]:[0-9][0-9]" /tmp/buildinfo/about.mappings)
#curdate=$(TZ='America/New_York' date +"%F %R")

echo "" > ${BPLOG}

cd ${DATADIR}
echo "<html><head><title>BridgePoint build</title></head><body><pre>"  >> ${BPLOG}
echo "Last Build:" >> ${BPLOG}
echo "===============" >> ${BPLOG}
echo "Version: $ver" >> ${BPLOG}
echo "Build ID: $curdate EDT" >> ${BPLOG}
#echo " " >> ${BPLOG}
#echo "Files:" >> ${BPLOG}
#echo "===============" >> ${BPLOG}
#ls -l >> ${BPLOG}
echo " " >> ${BPLOG}
echo "MD5 Sums:" >> ${BPLOG}
echo "===============" >> ${BPLOG}
md5sum *.zip >> ${BPLOG}
echo " " >> ${BPLOG}
echo "Links:" >> ${BPLOG}
echo "===============" >> ${BPLOG}
echo "<a href="org.xtuml.bp.product-linux.gtk.x86_64.zip">Linux 64-bit</a><br><a href="org.xtuml.bp.product-macosx.cocoa.x86_64.zip">Mac 64-bit</a><br><a href="org.xtuml.bp.product-win32.win32.x86.zip">Windows 32-bit</a><br><a href="org.xtuml.bp.product-win32.win32.x86_64.zip">Windows 64-bit</a><br></pre></body></html>" >> ${BPLOG}
export TEST_REPORT=$ARCHIVEDIR/org.xtuml.bp.releng.parent/target/site/surefire-report.html
if [ ! -f $TEST_REPORT ]; then
  export TEST_REPORT=/build/buildmt/hudson-home/jobs/BridgePoint/workspace/bridgepoint/releng/org.xtuml.bp.releng.parent/target/site/surefire-report.html
fi
if [ -f $TEST_REPORT ]; then
  cp  $TEST_REPORT $DATADIR
  echo " " >> ${BPLOG}
  echo "Testing:" >> ${BPLOG}
  echo "<br>===============" >> ${BPLOG}
  echo "<br><a href="surefire-report.html">Unit Test Results</a>" >> ${BPLOG}
fi

BRANCH_BUILD=$FORK-$BRANCH
if [ "$BRANCH_BUILD" == "xtuml-master" ]; then
  BRANCH_BUILD="nightly-build"
fi

export AWS_CONFIG_FILE=/build/buildmt/awsconfig
aws s3 sync ${DATADIR}/ s3://xtuml-releases/${BRANCH_BUILD}/  --grants read=uri=http://acs.amazonaws.com/groups/global/AllUsers
